package com.example.monitoringtanahsawah.helperimport android.content.Contextclass SharedPreference(context: Context) {    companion object {        private const val NAME = "name"        private const val LOGIN_STATE = "login_state"    }    private val preference = context.getSharedPreferences(NAME, Context.MODE_PRIVATE)    private val editor = preference.edit()    fun saveLogin(state: Boolean){ // fungsi simpan keadaan login        editor.apply {            putBoolean(LOGIN_STATE, state)            apply()        }    }    fun getLogin(): Boolean { // mendapatkan state login        return preference.getBoolean(LOGIN_STATE, false)    }}